import React from 'react'


const SearchListItem = ({course, cart, setCart}) => {

    let getId = () => {
        return course.id
    }

    let getTitle = () => {
        return course.title
    }

    let addCourse = () => {
        const reducer = (prev, curr) => {
            return prev || (curr.id === course.id)
        }
        console.log(!cart.reduce(reducer))
        if (cart.length === 0 || !cart.reduce(reducer)) {
            const newCart = [...cart]
            newCart.push(course)
            setCart(newCart)
        }
    }
    return (
        <div className = "search-list-item">
            <button onClick = {() => addCourse()}> + </button>
            <h3>{getId()}</h3>
            <p>{getTitle()}</p>
        </div>
    )
}

export default SearchListItem
