import React from 'react'
import GridLines from './GridLines'
import Days from './Days'
import Times from './Times'

const Schedule = () => {

    let startHour = 8.3;
    let endHour = 16;

    const rowOffset = 1;
    const colOffset = 1;

    const getNumRows = () => (Math.ceil(endHour) - Math.floor(startHour)) * 2 + rowOffset;
    const getNumCol = () => 5 + colOffset ;

    const dims = {
        gridTemplateColumns: `.4fr repeat(${getNumCol() - 1}, 1fr)`,
        gridTemplateRows: `repeat(${getNumRows()}, 1fr)`,
    };

    return (
        <div className="schedule box" style={dims}>
        <Days offset={colOffset} weekend={false} />
        <Times
            startTime={8.3}
            endTime={22}
            numRow={getNumRows()}
            offset={rowOffset}
        />
        <GridLines
            numRow={getNumRows()}
            numCol={getNumCol()}
        />
        {/* {blocks} */}
    </div>
    )
}

export default Schedule;
