import React, {useEffect} from 'react'
import Grid from './Grid'
import Blocks from './Blocks'


const Schedule = ({timeTables}) => {
    var position = 0

    const getBoundryTimes = (timetable) => {
        var min = 11
        var max = 3

        for (let i = 0; i < timetable.length; i++) {
            const sections = Object.values(timetable[i].ids)
            for (let j = 0; j < sections.length; j++){
                for (let k = 0; k < sections[j].length; k++) {
                    const start = parseFloat(sections[j][k][0].substring(1))
                    if (start < min) min = start
                    const end = parseFloat(sections[j][k][1].substring(1))
                    if (end > max) max = end
                }
            }
        }
        console.log(min)
        console.log(max)

        return [min, max]
    }

    var startTime
    var endTime

    [startTime, endTime] = getBoundryTimes(timeTables[0])

    return (
    <div className = "schedule-container">
            <Grid startTime={startTime} endTime = {endTime}/>
            <Blocks startTime={startTime} endTime = {endTime}/>
    </div>
    )
}

export default Schedule;
