import React from 'react'


const Schedule = ({timeTables}) => {
    var position = 0

    var startTime = 8.3
    var endTime = 16.0

    var numCols = 6
    var numRows = Math.ceil(endTime) - Math.floor(startTime) + 1


    const generateGridHeader = () => {
        return (
            [<div className = "grid-header"/>,
            <div className = "grid-header">Mon</div>,
            <div className = "grid-header">Tue</div>,
            <div className = "grid-header">Wed</div>,
            <div className = "grid-header">Thu</div>,
            <div className = "grid-header">Fri</div>]
        )
    }

    const generateGridBody = () => {
        var cells = []
        for (let i = 0; i < (numRows-2); i++) {
            cells.push(<div className = "grid-sidebar">time</div>)
            for (let j = 0; j < numCols-1; j++) {
                cells.push(<div className = "grid-box" />)
            }
        }
        for (let i = 0; i < (numCols); i++) {
            cells.push(<div className = "grid-box-bottom"/>)
        }
        return cells
    }


    const generateGrid = () => {

        const gridStyle = {
            gridTemplateColumns: 'repeat(' + numCols + ',1fr)',
            gridTemplateRows: 'repeat(' + numRows + ',1fr)'
        };

        return (
            <div className = "schedule-grid" 
                style = {gridStyle}>
                    {generateGridHeader()}
                    {generateGridBody()}
            </div>
        )
    }

    return (
    <div className = "schedule-container">
            {generateGrid()}

    </div>
    )
}

export default Schedule;
