import React from 'react'


const Schedule = ({timeTables}) => {
    var position = 0

    var startTime = 8.3
    var endTime = 16.0

    var numCols = 6
    var numRows = Math.ceil(endTime) - Math.floor(startTime) + 1


    const generateGrid = () => {

        var cells = []
        for (let i = 0; i < (numCols-1) * (numRows-2); i++) {
            cells.push(<div className = "grid-box"/>)
        }
        for (let i = 0; i < (numCols-1); i++) {
            cells.push(<div className = "grid-box-bottom"/>)
        }

        const gridStyle = {
            gridTemplateColumns: 'repeat(' + numCols + ',1fr)',
            gridTemplateRows: 'repeat(' + numRows + ',1fr)'
        };

        return (
            <div className = "schedule-grid" 
                style = {gridStyle}>
                <div className = "grid-sidebar"
                    style = {{gridRow: 'span ' + numCols}}>
                    hello
                </div>
                <div className = "grid-header" 
                    style = {{gridColumn: '2/-1'}}>
                    world!
                </div>
                {cells}
            </div>
        )
    }

    return (
    <div className = "schedule-container">
            {generateGrid()}

    </div>
    )
}

export default Schedule;
