import React from 'react'


const Blocks = ({startTime, endTime, timetable}) => {

    // 1. find color
    // 2. find position
    // 3. find length

    var numRows = (Math.ceil(endTime) - Math.floor(startTime) + 1) * 4
    const numCols = 6

    const gridStyle = () => {
        return {
            gridTemplateColumns: 'repeat(' + numCols + ',1fr)',
        gridTemplateRows: 'repeat(' + numRows + ',1fr)'
        }
    };

    const getColor = (x) => {
        const colors = ["blue", "red", "aqua", "orange", "green", "pink", "sea", "indigo"];
        return colors[x % 8]
    }

    const dayToPos = (day) => {
        if (day === 'M') {
            return 2
        } else if (day === 'T') {
            return 3
        } else if (day === 'W') {
            return 4
        } else if (day === 'R') {
            return 5
        } else {
            return 6
        }
    }

    const timeToPos = (time) => {
        let hour = math.floor(time)
        let frac = parseInt(0.6 / (time-hour))
        return 5 + hour * 4 + frac
    }

    const generateBlocks = () => {
        let blocks = []
        for (let i = 0; i < timetable.length; i++) {
            let color = getColor(i)
            let sections = Object.values(timetable[i].ids)
            for (const [key, value] of Object.entries(timetable[i].ids)) {
                for (let k = 0; k < value.length; k++) {
                    let day = value[k][0][0]
                    let start = parseFloat(value[k][0].substring(1))
                    let end = parseFloat(value[k][1].substring(1))
                    let block = <div className = "class-block" style = {{
                        gridColumn: dayToPos(day), gridRow: timeToPos(start) + "/" + timeToPos(end), backgroundColor: color}}>
                        {key}
                    </div>

                    blocks.append(block)
                }
            }
        }
        return blocks
    }

    return (
        <div className = "schedule-blocks"
        style = {gridStyle(numRows, numCols)}>
            {/* <div className = "class-block" style = {{gridColumn: 2, gridRow: 3, backgroundColor: '#f5f6f7'}}>
                HIIII
            </div> */}
            {generateBlocks()}
        </div>
    )
}

export default Blocks;
