import React from 'react'


const Schedule = ({timeTables}) => {
    var position = 0

    var startTime = 8.3
    var endTime = 16.0

    var numCols = 6
    var numRows = ceil(endTime) - floor(startTime) + 1
    var numCells = (numCols-1)*(numRows-1)


    const generateGrid = () => {

        cells = []
        for (let i = 0; i < (numCols-1 * numRows-2); i++) {
            cells.push(<div className = "grid-box"/>)
        }
        for (let i = 0; i < (numCols-1); i++) {
            cells.push(<div className = "grid-box-bottom"/>)
        }

        return (
            <div className = "schedule-grid" 
                style = {`grid-template-columns: repeat(${numCols}, 1fr); grid-template-rows: repeat(${numRows}, 1fr)`}>
                <div className = "grid-sidebar">
                    hello
                </div>
                <div className = "grid-header">
                    world!
                </div>
                {cells}
            </div>
        )
    }

    return (
    <div className = "schedule-container">
            {generateGrid()}

    </div>
    )
}

export default Schedule;
